import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:5000");

const App = () => {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({
    passengerName: "",
    trainNumber: "",
    coachNumber: "",
    station: "",
    minutesBeforeArrival: "",
    cooliesNeeded: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/api/bookings").then((res) => setBookings(res.data));

    socket.on("updateBookings", (newBooking) => {
      setBookings((prev) => [...prev, newBooking]);
    });

    return () => socket.off("updateBookings");
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://localhost:5000/api/bookings", form);
    socket.emit("newBooking", res.data);
    setForm({ passengerName: "", trainNumber: "", coachNumber: "", station: "", minutesBeforeArrival: "", cooliesNeeded: "" });
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center text-white bg-black bg-opacity-60 p-6">
      <h1 className="text-3xl font-extrabold text-center text-yellow-400 mb-6">
        ðŸš† Coolie Booking Service
      </h1>

      <form
        className="w-full max-w-md p-6 bg-gray-800 bg-opacity-90 rounded-lg shadow-lg space-y-4"
        onSubmit={handleSubmit}
      >
        <input
          type="text"
          name="passengerName"
          placeholder="Passenger Name"
          value={form.passengerName}
          onChange={handleChange}
          className="w-full p-2 rounded bg-gray-700 text-white placeholder-gray-400"
          required
        />
        <input
          type="text"
          name="trainNumber"
          placeholder="Train Number"
          value={form.trainNumber}
          onChange={handleChange}
          className="w-full p-2 rounded bg-gray-700 text-white placeholder-gray-400"
          required
        />
        <input
          type="text"
          name="coachNumber"
          placeholder="Coach Number"
          value={form.coachNumber}
          onChange={handleChange}
          className="w-full p-2 rounded bg-gray-700 text-white placeholder-gray-400"
          required
        />
        <input
          type="text"
          name="station"
          placeholder="Station Name"
          value={form.station}
          onChange={handleChange}
          className="w-full p-2 rounded bg-gray-700 text-white placeholder-gray-400"
          required
        />
        <input
          type="number"
          name="minutesBeforeArrival"
          placeholder="Minutes Before Arrival"
          value={form.minutesBeforeArrival}
          onChange={handleChange}
          className="w-full p-2 rounded bg-gray-700 text-white placeholder-gray-400"
          required
        />
        <input
          type="number"
          name="cooliesNeeded"
          placeholder="Coolies Needed"
          value={form.cooliesNeeded}
          onChange={handleChange}
          className="w-full p-2 rounded bg-gray-700 text-white placeholder-gray-400"
          required
        />
        <button
          type="submit"
          className="w-full bg-yellow-500 text-black font-bold py-2 px-4 rounded hover:bg-yellow-600 transition"
        >
          Book Coolie ðŸš€
        </button>
      </form>

      <h2 className="text-2xl font-bold text-yellow-300 mt-6">ðŸ“‹ Active Bookings:</h2>
      <div className="w-full max-w-md bg-gray-800 bg-opacity-90 p-4 rounded-lg shadow-lg mt-4">
        {bookings.length === 0 ? (
          <p className="text-gray-400">No active bookings</p>
        ) : (
          bookings.map((b, index) => (
            <div key={index} className="p-2 bg-gray-700 text-white rounded mt-2 shadow">
              <strong>{b.passengerName}</strong> - Train {b.trainNumber}, Coach {b.coachNumber}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default App;
